import axios from 'axios';


axios.defaults.baseURL = "https://localhost:7190";
export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(itemName)=>{

    const result = await axios.post(`$/items`,{name:itemName,isComplete:false})    
    return result.data;    
  },

  setCompleted: async (id, isComplete) => {
    console.log("zxcfghjnmk")
    await axios.put(`${apiUrl}/items/${id}?isComplete=${isComplete}`)
  },
  deleteTask:async(id)=>{
    const result = await axios.delete(`${apiUrl}/items/${id}`)    
    return result.data;
  }
};
